import React, { useState } from "react";
import annyang from "annyang";
import ChatInput from "./ChatInput";
import Box from "@mui/material/Box";
import ChatData from "./ChatData";
import axios from "axios";

export const VoiceChatBot = ({ ipData }) => {
  const [chat, setChatData] = useState([]);
  const [aiData, setAiData] = useState("");

  function stopRecording() {
    if (annyang) {
      annyang.removeCallback("result", handleSpeechResult);
      annyang.abort();
    }
  }

  function startRecoding() {
    annyang.start();
    num_id = num;
    if (annyang) {
      annyang.start();
      annyang.addCallback("result", handleSpeechResult);
    }
  }

  async function sendPostData({ query, id }) {
    console.log("queru ", query);
    const response = await axios.post("http://127.0.0.1:5000/bard_response", {
      question: query,
    });
    setAiData(response.data);
  }

  const handleSpeechResult = (userSaid) => {
    // setText(userSaid);
    setChatData((prev) => [...prev, userSaid]);
    setTimeout(stopRecording, 1000);
  };

  const setTextInput = ({ query, id }) => {
    // const setData = chat.map((item) => {
    //   if (item?.id === id) return { ...item, text: query };
    //   return item;
    // });

    setChatData((prev) => [...prev, { id: id, text: query, ai: "" }]);
    console.log("id ", id);
    // setChatData(setData);
  };

  console.log("chat in ", chat);
  return (
    <Box
      sx={{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        gap: 5,
        marginTop: 5,
      }}
    >
      <Box>
        <ChatInput
          startRecoding={startRecoding}
          sendPostData={sendPostData}
          setTextInput={setTextInput}
        />
      </Box>
      <Box
        sx={{
          height: 1000,
          width: 1000,
          border: "1px solid black",
          borderRadius: "10px",
          fontFamily: "inherit",
        }}
      >
        {chat.length > 0 && <ChatData ipData={ipData} chat={chat} />}
      </Box>
    </Box>
  );
};

const TextDisplay = ({ text }) => {
  return (
    <div>
      {/* <h2>Converted Text</h2> */}
      <p>{text}</p>
    </div>
  );
};

export default VoiceChatBot;
