import React, { useState } from "react";
import annyang from "annyang";
import ChatInput from "./ChatInput";
import Box from "@mui/material/Box";
import ChatData from "./ChatData";

export const VoiceChatBot = () => {
  // const [text, setText] = useState("");
  const [chat, setChatData] = useState([]);

  function stopRecording() {
    if (annyang) {
      annyang.removeCallback("result", handleSpeechResult);
      annyang.abort();
    }
  }

  function startRecoding() {
    annyang.start();

    if (annyang) {
      annyang.start();
      annyang.addCallback("result", handleSpeechResult);
    }
  }

  const handleSpeechResult = (userSaid) => {
    // setText(userSaid);
    setChatData((prev) => [...prev, userSaid]);
    setTimeout(stopRecording, 1000);
  };

  const setTextInput = (text) => {
    setChatData((prev) => [...prev, text]);
  };

  return (
    <Box
      sx={{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        gap: 5,
        marginTop: 5,
      }}
    >
      <Box>
        <ChatInput startRecoding={startRecoding} setTextInput={setTextInput} />
      </Box>
      <Box
        sx={{
          height: 1000,
          width: 1000,
          border: "1px solid black",
          borderRadius: "10px",
          fontFamily: "inherit",
        }}
      >
        {chat.length > 0 && <ChatData chat={chat} />}
      </Box>
    </Box>
  );
};

const TextDisplay = ({ text }) => {
  return (
    <div>
      {/* <h2>Converted Text</h2> */}
      <p>{text}</p>
    </div>
  );
};

export default VoiceChatBot;
